import React, { useState } from "react";
import "../../styles/weatherStyles.css";

function Weather() {
  const api = {
    key: "bfee40a57b03b378c6f6fc873967f983",
    url: "https://api.openweathermap.org/data/2.5/",
  };

  const [query, setQuery] = useState("");
  const [weather, setWeather] = useState({});
  // const [eventSuggestions, setEventSuggestions] = useState({});
  let isUserSearchingFirstTime = true;
  const [lat, setLat] = useState("");
  const [lon, setLon] = useState("");

  const search = (evt) => {
    if (evt.key === "Enter") {
      fetch(`${api.url}weather?q=${query}&appid=${api.key}&units=metric`)
        .then((res) => res.json())
        .then((result) => {
          setWeather(result);
          setLat(result.coord.lat);
          setLat(result.coord.lon);
          setQuery("");
          console.log(result);
          console.log("lat: " + lat + "lon: " + lon);
        });
    }
  };

  const getTodaysDate = (d) => {
    const months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    const days = [
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
      "Sunday",
    ];

    var day = days[d.getDay()];
    var date = d.getDate();
    var month = months[d.getMonth()];
    var year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`;
  };

  const switchToOldUser = () => {
    isUserSearchingFirstTime = false;
    return `Welcome`;
  };

  return (
    <div>
      <main>
        <input
          type="text"
          className="search-bar"
          placeholder="Enter your city"
          onChange={(e) => setQuery(e.target.value)}
          value={query}
          onKeyPress={search}
        ></input>
        {typeof weather.main != "undefined" ? (
          <div>
            <div className="weather-container">
              <div className="weather">
                <div className="temp">{Math.round(weather.main.temp)}Â°C</div>
                <div className="condition">{weather.weather[0].main}</div>
                <div className="city">
                  {weather.name}, {weather.sys.country}
                </div>
                <br></br>
                <div className="date">{getTodaysDate(new Date())}</div>
                <br></br>
              </div>
              <aside>Activites Here</aside>
            </div>
          </div>
        ) : (
          <div>
            <h1>{weather.message}</h1>
          </div>
        )}
      </main>
    </div>
  );
}

export default Weather;

