    }

    _createWireframeGrid(): LineIndexArray {
        const indexArray = new LineIndexArray();

        const quadExt = GLOBE_VERTEX_GRID_SIZE;
        const vertexExt = quadExt + 1;

        const quad = (i, j) => {
            const index = j * vertexExt + i;
