import { useState, useEffect } from "react";
import Map, { Popup, Marker } from "react-map-gl";
import Axios from "axios";

export default function Favourites() {
  const [geopins, setGeopins] = useState([]);
  const [selectedFavourites, setSelectedFavourites] = useState(null);
  const favouriteMarkers = [];

  const fetchFavourites = async (name) => {
    // let name = await window.localStorage.getItem("username");
    // fetch(`http://localhost:3022/API/v1/GetFav?username=admin`)
    Axios.get("http://localhost:3022/API/v1/GetFav", {
      params: { username: name },
    })
      .then((res) => res)
      .then((result) => {
        for (let key in result.data.rows) {
          let objData = result.data.rows[key];
          favouriteMarkers.push({
            longitude: objData.fav_lng,
            latitude: objData.fav_lat,
            name: objData.fav_item_name,
          });
          break;
        }
        //     for (let internalKey in result.data[key]) {
        //
        //
        // }
        setGeopins(favouriteMarkers);
      });
  };

  const addMarkers = () => {
    return geopins.map((ithMarker) => {
      return (
        <Marker
          key={`marker-${ithMarker.id}`}
          id={ithMarker.name}
          latitude={ithMarker.latitude}
          longitude={ithMarker.longitude}
        >
          <button
            key={`markerButtonKey-${ithMarker.id}`}
            id={`markerButtonId-${ithMarker.id}`}
            style={{ background: "none", border: "none", cursor: "pointer" }}
            onClick={() => {
              setSelectedFavourites(ithMarker);
            }}
          >
            <img
              style={{ height: "25px" }}
              src="./building.svg"
              alt={ithMarker.name}
            />
          </button>
        </Marker>
      );
    });
  };

  // const addToFavourites = (ithMarker) => {
  //   let isDuplicate = false;
  //   for (let key in geopins) {
  //     if (geopins[key]["id"] == ithMarker["id"]) {
  //       isDuplicate = true;
  //     }
  //   }
  //   if (!isDuplicate) {
  //     favouriteBuildings.push(ithMarker);

  //     window.alert("Added!");
  //   } else {
  //     window.alert("Already exists");
  //   }
  // };

  const addPopup = () => {
    return geopins.map((ithMarker) => {
      return selectedFavourites && selectedFavourites.id == ithMarker.id ? (
        <Popup
          key={`popup-${ithMarker.id}`}
          id={ithMarker.id}
          latitude={ithMarker.latitude}
          longitude={ithMarker.longitude}
          offsetTop={-30}
        >
          <div>
            <p>{ithMarker.name}</p>
            <button
              onClick={() => {
                Axios.delete("http://localhost:3022/API/v1/DelFav", {
                  username: window.localStorage.getItem("username"),
                  favName: ithMarker.favName,
                  favCat: ithMarker.favCat,
                  lat: ithMarker.lat,
                  lng: ithMarker.lng,
                });

                window.alert("Deleted!");
              }}
            >
              Delete from favourites
            </button>
          </div>
        </Popup>
      ) : null;
    });
  };

  useEffect(() => {
    let name = window.localStorage.getItem("username");
    console.log(name);
    fetchFavourites(name);
  }, []);

  return (
    <Map
      initialViewState={{
        longitude: -123.19258095208848,
        latitude: 49.25901995475811,
        zoom: 11,
      }}
      style={{
        width: "80vw",
        height: "80vh",
        fog: {
          range: [-0.5, 3],
          color: "white",
          "horizon-blend": 0.1,
        },
      }}
      mapStyle="mapbox://styles/mapbox/streets-v9"
      mapboxAccessToken="pk.eyJ1IjoiYmh1cGVzaGQiLCJhIjoiY2wxNXN1ZWVwMGoxNTNjcDhkZHh5Z2NsaCJ9.G67FI-pS8DZRWCX5Nt1vrA"
    >
      {addMarkers()}
      {addPopup()}
    </Map>
  );
}
