import React, { useEffect, useState } from "react";
import { useNavigate } from 'react-router-dom';
import Axios from 'axios';
import styled from 'styled-components';

function MyInfo() {
  const [username, setUsername] = useState('')
  const [location, setLocation] = useState('')
  const [changeLocation, setChangeLocation] = useState(false)
  const [newLocation, setNewLocation] = useState('')

  useEffect(() => {
    setUsername(window.localStorage.getItem("username"))
    let location_id = window.localStorage.getItem("location")

    switch (location_id){
      case '1':
        setLocation("Vancouver")
        break

      case '2':
        setLocation("Burnaby")
        break

      case '3':
        setLocation("Richmond")
        break

      case '4':
        setLocation("North Vancouver")
        break

      case '5':
        setLocation("Abbotsford")
        break

      case '6':
        setLocation("Coquitlam")
        break

      case '7':
        setLocation("Kamloops")
        break

      case '8':
        setLocation("Langley")
        break

      case '9':
        setLocation("Maple Ridge")
        break

      case '10':
        setLocation("New Westminster")
        break

      case '11':
        setLocation("Surrey")
        break

      default:
        setLocation("Other")
    }
  }, [])

  const toggleList = () => {
    setChangeLocation(!changeLocation)
  }

  return (
    <MyInfoStyled>
      My Info here!
      <div id='container'>
        <p>UserName: {username}</p>
        <p>Location: {location}</p>
        <button onClick={toggleList}>Change Location</button>
        {changeLocation? (
          <>
          <br />
          <select id='locations'>
            <option>Vancouver</option>
            <option>Burnaby</option>
            <option>Richmond</option>
            <option>North Vancouver</option>
            <option>Abbotsford</option>
          </select>
          <br />
          <button>Set New Location</button>
          </>
        ):(
          <></>
        )}
      </div>
    </MyInfoStyled>
  );
}

const MyInfoStyled = styled.div`
  color: red;
`;
export default MyInfo;
