import React, { useEffect, useState } from "react";
import { useNavigate } from 'react-router-dom';
import Axios from 'axios';
import styled from 'styled-components';

function MyInfo() {
  const [username, setUsername] = useState('')
  const [location, setLocation] = useState('')

  useEffect(() => {
    setUsername(window.localStorage.getItem("username"))
    let location_id = window.localStorage.getItem("location")

    switch (location_id){
      case 1:
        setLocation("Vancouver")
        break

      case 2:
        setLocation("Burnaby")
        break

      case 3:
        setLocation("Richmond")
        break

      case 4:
        setLocation("North Vancouver")
        break

      case 5:
        setLocation("Abbotsford")
        break

      case 6:
        setLocation("Coquitlam")
        break

      case 7:
        setLocation("Kamloops")
        break

      case 8:
        setLocation("Langley")
        break

      case 9:
        setLocation("Maple Ridge")
        break

      case 10:
        setLocation("New Westminster")
        break

      case 11:
        setLocation("Surrey")
        break

      default:
        setLocation("Other")
    }
  }, [])

  return (
    <MyInfoStyled>
      My Info here!
      <div id='container'>
        <p>UserName: {username}</p>
        <p>Location: {location}</p>
      </div>
    </MyInfoStyled>
  );
}

const MyInfoStyled = styled.div`
  color: red;
`;
export default MyInfo;
