import React, { useState } from "react";
import "../../styles/weatherStyles.css";

function Weather() {
  const api = {
    key: "bfee40a57b03b378c6f6fc873967f983",
    url: "https://api.openweathermap.org/data/2.5/",
  };
  const [query, setQuery] = useState("");
  const [weather, setWeather] = useState({});
  const search = (evt) => {
    if (evt.key === "Enter") {
      fetch(`${api.url}weather?q=${query}&appid=${api.key}&units=metric`)
        .then((res) => res.json())
        .then((result) => {
          setWeather(result);
          setQuery("");
          console.log(result);
        });
    }
  };

  const getTodaysDate = (d) => {
    const months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    const days = [
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
      "Sunday",
    ];

    var day = days[d.getDay()]; // Fetches the day of the week
    var date = d.getDate(); // Fetches the date i.e. 1st - 31st day of the month
    var month = months[d.getMonth()]; // Fetches the month
    var year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`;
  };

  return (
    <div>
      <main>
        <input
          type="text"
          className="search-bar"
          placeholder="Enter your city"
          onChange={(e) => setQuery(e.target.value)}
          value={query}
          onKeyPress={search}
        ></input>
        {typeof weather.main != "undefined" ? (
          <div>
            <div className="weather-container">
              <div className="weather">
                <div className="temp">{Math.round(weather.main.temp)}Â°C</div>
                <div className="condition">{weather.weather[0].main}</div>
                <div className="city">
                  {weather.name}, {weather.sys.country}
                </div>
                <br></br>
                <div className="date">{getTodaysDate(new Date())}</div>
                <br></br>
              </div>
              <aside style={{ color: yellow }}>Activites Here</aside>
            </div>
          </div>
        ) : (
          <div>
            <h1>
              <span>
                <b>OOPS!</b>
              </span>{" "}
              Looks like the place you searched for isn't on planet Earth.
            </h1>
          </div>
        )}
      </main>
    </div>
  );
}

export default Weather;
