import React from "react";
import styled from "styled-components";
import Axios from "axios";
import { InputGroup, FormControl, Button } from "react-bootstrap";

function ContactUs() {
  const send = async () => {
    let msg = document.getElementById("content").value;
    if (!msg) {
      window.alert("Message is empty");
      return;
    }
    console.log(msg);
    let name = await window.localStorage.getItem("username");
    Axios.post("http://localhost:3022/API/v1/ContactUs", {
      username: name,
      msg: msg,
    }).then((res) => {
      if (res.status == 200) {
        document.getElementById("reply").innerHTML =
          "Thank you for contacting us, we will get back to you within 3 days";
        document.getElementById("content").value = "";
      }
    });
  };

  return (
    // <ContactUsStyled>
    //   <input type="text" id="content" />
    //   <button onClick={send}>Send Message</button>
    //   <p id='reply'></p>
    // </ContactUsStyled>
    <InputGroup className="mb-3">
      <FormControl
        placeholder="Type your message..."
        aria-label="Type your message..."
        aria-describedby="basic-addon2"
        id="content"
      />
      <Button variant="outline-secondary" id="button-addon2" onClick={send}>
        Send Message
      </Button>
      <div>
        <p id="reply"></p>
      </div>
    </InputGroup>
  );
}

const ContactUsStyled = styled.div`
  color: red;
`;

export default ContactUs;
