import React, { useEffect, useState } from "react";
import { useNavigate } from 'react-router-dom';
import Axios from 'axios';

function SignUp() {
  const navigate = useNavigate();
  const [login, setLogin] = useState(false);
  const [signUp, setSignUp] = useState(true);

  useEffect(() => {
    if (window.localStorage.getItem("login") === "true") {
      setLogin(true);
    }

  }, []);

  const loginFunc = async() => {
    console.log("login")
  }

  const changeSignUp = () => {
    setSignUp(false)
  }

  const signupFunc = async() => {
    console.log("signup")
    let username = document.getElementById("name").value
    let password = document.getElementById("password").value
    let age = document.getElementById("age").value
    let location = document.getElementById("location-select").value
    switch (location){
      case 'Vancouver':
        location = 1
        break

      case 'Burnaby':
        location = 2
        break

      case 'Richmond':
        location = 3
        break

      case 'North Vancouver':
        location = 4
        break

      case 'Abbotsford':
        location = 5
        break

      case 'Coquitlam':
        location = 6
        break

      case 'Kamloops':
        location = 7
        break

      case 'Langley':
        location = 8
        break

      case 'Maple Ridge':
        location = 9
        break

      case 'New Westminster':
        location = 10
        break

      case 'Surrey':
        location = 11
        break

      default:
        location = 12
    }

    const values = {
      newUsername: username,
      newUserPassword: password,
      newUserAge: age,
      newUserLocation: location
    }

    Axios.post('http://localhost:3022/SignUp', {
      newUserName: values.newUsername,
      newUserPassword: values.newUserPassword,
      newUserAge: values.newUserAge,
      newUserLocation: values.newUserLocation,
    })
    .then(res => {
      if (res.status == 200) {
        window.localStorage.setItem("login", true)
        navigate("/")
      }
    })


  }

  return (
    <div style={{ backgroundColor: "green", width: "100%", height: "80%" }}>

      <div id='nameContainer'>
        <label for="name">UserName</label>
        <input type='text' id='name'/>
      </div>

      <div id='passwordContainer'>
        <label for="password">Password</label>
        <input type='text' id='password' />
      </div>

      {login & !signUp ? (
        <button onClick={loginFunc}>Login</button>
      ) : (
        <>
        <div id='ageContainer'>
          <label for="age">Age</label>
          <input type='number' id='age' />
        </div>

          <div id='locationContainer'>
            <label for="location-select">Location</label>
            <select id='location-select'>
              <option>-- Please select your location --</option>
              <option>Vancouver</option>
              <option>Burnaby</option>
              <option>North Vancouver</option>
              <option>Abbotsford</option>
              <option>Coquitlam</option>
              <option>Kamloops</option>
              <option>Langley</option>
              <option>Maple Ridge</option>
              <option>New Westminster</option>
              <option>Surrey</option>
              <option>Other</option>
            </select>
          </div>

        <button onClick={signupFunc}>SignUp</button>
        <p onClick={changeSignUp}>Login</p>
        </>
      )}
    </div>
  );
}

export default SignUp;
